mapper.select:
SELECT FROM t_user
AND loginacct LIKE concat('%',#{queryText},'%')
ORDER BY createtime DESC limit #{startIndex},#{pageSize}

mapper.insert:
INSERT INTO t_user (
  loginacct,username,userpswd,email,createtime
) VALUES (
  #{loginacct},#{username},#{userpswd},#{email},#{createtime}
)

    SELECT @@IDENTITY AS id
